package com.ipseorama.base.dataChannel;
import org.bouncycastle.crypto.tls.Certificate;
import biz.source_code.Base64Coder;
/**
 *
 * @author tim
 */
public class InvestigateCert {
    static String chromeCert1 = "";
    static String chromeCert2 = "";
    static String chromeCert3 = "";
    static String chromeCert4 = "";
    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        printCN(chromeCert1);
        printCN(chromeCert2);
        printCN(chromeCert3);
        printCN(chromeCert4);

    }
    
    public static void printCN(String cert){
        byte [] certBytes =   Base64Coder.decode(cert);
        org.bouncycastle.asn1.x509.Certificate c = org.bouncycastle.asn1.x509.Certificate.getInstance(certBytes);
        System.out.print( c.getSubject().toString()); 
        System.out.println(" Issuer " +c.getIssuer().toString());
    }
    
}
